import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function MemberList() {
    const [members, setMembers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const navigate = useNavigate();

    const fetchData = async () => {
        try {
            setIsLoading(true);
            const response = await fetch('/api/member');
            const data = await response.json();
            setMembers(data);
            setIsLoading(false);
            console.log(data);
        } catch (error) {
            console.error('Error fetching data:', error);
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    if (isLoading) {
        return (
            <div>
                <p>Loading...</p>
            </div>
        );
    }

    const handleRefresh = () => {
        fetchData();
    }

    const handleRowClick = (id) => {
        navigate(`member/${id}`);
    };

    return (
        <div>
            <h4>Member List</h4>
            <button onClick={handleRefresh}>Refresh</button>
            <table border="1">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Date of Creation</th>
                    </tr>
                </thead>
                <tbody>
                    {members.map((member) => (
                        <tr key={member.id} onClick={() => handleRowClick(member.id)}>
                            <td>{member.id}</td>
                            <td>{member.name}</td>
                            <td>{member.dateOfCreation}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default MemberList;
